import React,{useEffect,useState} from 'react'
import axios from 'axios'
const end ='http://localhost:3004/promotions?_'

const ApiExterno = () => {
    const [tabela, setTabela]=useState('')
    console.log(tabela)

    useEffect(()=>{
        axios.get('http://localhost:3004/promotions?_embed=comments').then((resp)=>{
            setTabela(resp.data)
        })
    }, [])

    return(
        <div>
            <img src={tabela.imageUrl} alt={tabela.title}/>
            <div>
                <h1>{tabela.title}</h1>
                <span>R$: {tabela.price} </span>
                <footer>
                    {tabela.comments.length > 0 && (
                        <div>
                            {tabela.comments[0].comment}
                        </div>
                    )}
                    <div>
                        {tabela.comments.length} comentarios
                    </div>
                    <a href={tabela.url} target='blank' >Ir para o site</a>
                </footer>
            </div> 
        </div>
    )
}
export default ApiExterno;
