        "comments": [
          {
            "id": 1,
            "comment": "TELA HD"
          }
        ]
      }

    return(
        <div className='loja_conteiner' >

            {apiLoja1.map(AP)}

            <ApiEsterna promotions={promotions} />
        </div>
    )
}
export default Loja1;
